import java.util.Scanner;

public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row=4,col=10;
		for(int i=0;i<row;i++)
		{
			for(int j=0;i<col;i++) {
				System.out.println("*");
			}
			System.out.println();
		}
	}
}
	
